css:
.print-format {
	margin: 0;
	padding: 0;
    margin-left: 1mm;
    margin-right: 1mm;
    margin-top: 1mm;
    margin-bottom: 1mm;
	width: 100mm;
	max-height: 39mm;
}

.page-size {
	width: 104mm;
	height: 40mm;
}

.label-wrapper {
	width: 104mm;
	text-align: center;
	display: -webkit-box;
	/* wkhtmltopdf uses this one */
	display: -webkit-flex;
	display: flex;
	-webkit-box-pack: center;
	/* wkhtmltopdf uses this one */
	-webkit-justify-content: center;
	justify-content: center;
	font-size: 15px;
}

.left-wrapper {
	width: 80mm;
	margin-top: 1mm;
}

.left-wrapper div {
	white-space: nowrap;
	font-size: 12px;
}

.right-wrapper {
	width: 20mm;
	text-align: right;
}

.label-wrapper h4 {
	margin: 0;
	white-space: nowrap;
	font-size: 23px;
}

.label-wrapper h5 {
	margin: 0;
	white-space: nowrap;
	font-size: 15px;
}


.right-wrapper {
    padding-right: 2mm;
    padding-top: 0.5mm;
}

html:
{% if doc.is_warehouse_label %}
<div class="label-wrapper">
   <div class="left-wrapper">
      {% if doc.customer %} 
      <h5 style="font-size: 20px;"><strong>{{doc.customer}}</strong></h5>
      {% endif %}
      {% if doc.item_code %} 
      <h4 style="font-size: 25px;"><strong>{{doc.item_code}}</strong></h4>
      {% endif %}
      {% if doc.item_name%} 
      <h5 style="font-size: 20px;">{{doc.item_name}}</h5>
      {% endif %}

      {% if doc.batch %}
      <div><strong>{{_("Batch")}} </strong> {{doc.batch}}</div>
      {% endif %}

      {% if doc.information%} 
      <div> {{doc.information}} </div>
      {% endif %} 
   </div>
</div>

{% else %}
<div class="label-wrapper">
   <div class="left-wrapper">
      {% if doc.customer %} 
      <h5><strong>{{doc.customer}}</strong></h5>
      {% endif %}
      {% if doc.item_code %} 
      <h4><strong>{{doc.item_code}}</strong></h4>
      {% endif %}
      {% if doc.item_name%} 
      <h5>{{doc.item_name}}</h5>
      {% endif %}
      {% if doc.delivery_date %} 
      <div> <strong> {{_("Delivery Date")}} </strong> {{frappe.utils.formatdate(doc.delivery_date, "dd.MM.YYYY")}} </div>
      {% endif %}
      {% if doc.qty %} 
      <div> <strong>{{_("Qty")}} </strong> {{ doc.qty }}</div>
      {% endif %}
      {% if doc.batch %}
      <div><strong>{{_("Batch")}} </strong> {{doc.batch}}</div>
      {% endif %}

      {% if doc.information%} 
      <div> {{doc.information}} </div>
      {% endif %} 
   </div>
   <div class="right-wrapper"> <img src="/files/hp_logo_black.jpg" style="width: 30px;"> </div>
</div>
{% endif %}
