css:
.print-format {
	margin: 0;
	padding: 0;
    margin-left: 1mm;
    margin-right: 1mm;
    margin-top: 1mm;
    margin-bottom: 1mm;
	width: 100mm;
	max-height: 39mm;
}

.page-size {
	width: 104mm;
	height: 40mm;
}

.label-wrapper {
	width: 100mm;
	text-align: center;
	display: -webkit-box;
	/* wkhtmltopdf uses this one */
	display: -webkit-flex;
	display: flex;
	-webkit-box-pack: center;
	/* wkhtmltopdf uses this one */
	-webkit-justify-content: center;
	justify-content: center;
	font-size: 15px;
}

.left-wrapper {
	width: 80mm;
}

.left-wrapper div {
	white-space: nowrap;
	font-size: 12px;
}

.right-wrapper {
	width: 20mm;
	text-align: right;
}

.label-wrapper h4 {
	margin: 0;
	word-break: break-all;
	font-size: 23px;
}

.label-wrapper h4 small {
	color: black;
}

html:
{% if doc.customer %}
<div class="label-wrapper">

  <div class="left-wrapper">
    {% if doc.item_code %}
    <h4>
      <strong>{{doc.item_code}}</strong>
    </h4>
    {% endif %}

    {% if doc.item_name%}
    <h4>
      <small> {{doc.item_name}}</small>
    </h4>
    {% endif %}


    {% if doc.customer %}
    <div>{{doc.customer}}</div>
    {% endif %}

    {% if doc.delivery_date %}
    <div>
      <strong> {{_("Delivery Date")}} </strong> {{frappe.utils.formatdate(doc.delivery_date, "dd.MM.YYYY")}}
    </div>
    {% endif %}

    {% if doc.batch %}
    <div>
      <strong>{{_("Batch")}} </strong> {{doc.batch}}
    </div>
    {% endif %}

    {% if doc.qty %}
    <div>
      <strong>{{_("Qty")}} </strong> {{ doc.qty }}
    </div>
    {% endif %}

    {% if doc.information%}
    <div>
     {{doc.information}}
    </div>
    {% endif %}


  </div>

   <div class="right-wrapper">
    <img src="/files/hp_logo_black.jpg" style="width: 30px;">
  </div>
</div>

{% else %}

<div class="label-wrapper">

  <div class="left-wrapper">
    {% if doc.item_code %}
    <h4 style="font-size: 30px; white-space: normal; padding: 2mm;">
      <strong>{{doc.item_code}}</strong>
    </h4>
    {% endif %}


    {% if doc.batch %}
    <div style="font-size: 15px;">
      <strong>{{_("Batch")}} </strong> {{doc.batch}}
    </div>
    {% endif %}


    {% if doc.information%}
    <div>
     {{doc.information}}
    </div>
    {% endif %}


  </div>
</div>

{% endif %}
